    <style>
        /* Estilo para la galería de prestadores (reemplazo del marquee) */
        .prestadores-grid {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(120px, 1fr));
            gap: 20px;
            padding: 20px 0;
            justify-items: center;
        }
        .prestadores-grid img {
            width: 100px;
            height: auto;
            filter: grayscale(100%);
            transition: filter 0.3s ease;
        }
        .prestadores-grid img:hover {
            filter: grayscale(0%);
        }

        /* Estilo para el botón flotante de WhatsApp */
        .whatsapp-float {
            position: fixed;
            width: 60px;
            height: 60px;
            bottom: 40px;
            right: 40px;
            background-color: #25d366;
            color: #fff;
            border-radius: 50px;
            text-align: center;
            font-size: 30px;
            box-shadow: 2px 2px 3px #999;
            z-index: 100;
            display: flex;
            align-items: center;
            justify-content: center;
        }
        .whatsapp-float:hover {
            background-color: #128C7E;
        }

        /* Estilo para la sección de empresas */
        .lista-tipo-4 .item {
            border: 1px solid #eee;
            padding: 20px;
            margin-bottom: 20px;
            border-radius: 8px;
            transition: transform 0.2s ease;
        }
        .lista-tipo-4 .item:hover {
            transform: translateY(-5px);
            box-shadow: 0 5px 15px rgba(0,0,0,0.1);
        }
        .inv-cob {
            display: flex;
            justify-content: space-between;
            margin: 15px 0;
            font-weight: bold;
        }
        .botao a {
            background-color: #0042DA;
            color: white;
            padding: 10px 20px;
            border-radius: 5px;
            text-decoration: none;
            font-weight: bold;
            display: inline-block;
            transition: background-color 0.3s ease;
        }
        .botao a:hover {
            background-color: #0033a0;
        }

        /* Loader del formulario */
        .loader-whats {
            display: none;
            width: 52px;
            height: 52px;
            background-color: white;
            background-image: url(./assets/css/gif-enviando.gif);
            background-position: center;
            background-repeat: no-repeat;
            background-size: 40px;
            border-radius: 100%;
            position: relative;
            left: 50%;
            bottom: 20px;
            margin: 0 0 0 -26px;
            box-shadow: 0 0 10px rgba(0,0,0,0.5);
        }

        /* Botón principal destacado */
        .cta-principal {
            background-color: #FF6B00 !important;
            color: white !important;
            padding: 15px 30px !important;
            font-size: 18px !important;
            border-radius: 50px !important;
